// NNStat.cpp////// This is intended to be used with other programs for generating test data// sets for NearTree. NNStat is designed to provide some basic measures// about the data sets for NearTree.//#include <iostream>#include <cmath>#include "TNear.h"#include "Data2CSV.h"/*+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/int main(int argc, char* argv[])//---------------------------------------------------------------------{    std::vector<vecN> v = ReadGeneralFile( std::cin );    CNearTree<vecN> nt( v );    const double hausdorff = nt.GetDimEstimate( );    const long depth = (long)nt.GetDepth( );    fprintf( stdout, " v.size %d\n  depth %ld\n dimension %d\n estimated-dimension %g\n",         nt.size( ), depth, v[0].dim, hausdorff );    bool bCopyInput = false;    if ( bCopyInput )    {        WriteVectorFile( v );    }	return 0;}